#include <iostream>using namespace std;#define MAXN 1010#define MAXV 10010int n, m, moedas[MAXN], resp=0, matriz[MAXN][MAXV], ver[MAXV];void troco(int atual, int soma){    if(matriz[atual][soma] || atual>n || soma>m)    	return;        matriz[atual][soma]=1;    ver[soma]=1;        troco(atual, soma+moedas[atual]);    troco(atual+1, soma);}int main(){      cin >> n >> m;        for(int i=1; i<=n; i++) cin >> moedas[i];        troco(1,0);        for(int i=1; i<m; i++){     //cout<< i << " = " << ver[i]<<"\n";    	resp+=ver[i];    }        cout << m-resp -1<< "\n";        return 0;}